import React, { Component } from 'react';
import axios from 'axios';
import Select from 'react-select';
import { Bar } from 'react-chartjs-2';
import './App.css';

class App extends Component {
  state = {
    data: null,
    selectedOption: '',
    searchSuggestionsArr: [],
    searchResultsArr: [],
    noAutoCompleteDuplicateObj: {},
    barData: {
      labels: [75, 76, 77, 78, 79, 80, 81, 82, 83, 84, 85, 86, 87, 88, 89, 90, 91, 92, 93, 94, 95, 96, 97, 98, 99, 100],
      datasets: [
        {
          label: 'Books with this review score',
          backgroundColor: 'rgba(255,99,132,0.2)',
          borderColor: 'rgba(255,99,132,1)',
          borderWidth: 1,
          hoverBackgroundColor: 'rgba(255,99,132,0.4)',
          hoverBorderColor: 'rgba(255,99,132,1)',
          data: [1, 2, 3, 4, 5, 6, '7+']
        }
      ]
    }
  };

  buildSearchSuggestionsArr = () => {
    const searchSuggestionsArr = [];
    const noAutoCompleteDuplicateObj = {};

    for (let i = 1; i <= this.state.searchResultsArr.length - 1; i++) {

      //this is to prevent duplicate auto suggestions
      if (!noAutoCompleteDuplicateObj[this.state.searchResultsArr[i].author]) {
        searchSuggestionsArr.push({ label: 'Author: ' + (this.state.searchResultsArr[i].author || 'Anonymous'), value: this.state.searchResultsArr[i].author });
        noAutoCompleteDuplicateObj[this.state.searchResultsArr[i].author] = [];
      }

      //assuming no duplicate titles
      if (!noAutoCompleteDuplicateObj[this.state.searchResultsArr[i].title]) {
        searchSuggestionsArr.push({ label: 'Title: ' + this.state.searchResultsArr[i].title, value: this.state.searchResultsArr[i].title });
        noAutoCompleteDuplicateObj[this.state.searchResultsArr[i].title] = [];
      }

      //this is to prevent duplicate auto suggestions
      if (!noAutoCompleteDuplicateObj[this.state.searchResultsArr[i].review_score]) {
        searchSuggestionsArr.push({ label: 'Review Score: ' + this.state.searchResultsArr[i].review_score, value: this.state.searchResultsArr[i].review_score });
        noAutoCompleteDuplicateObj[this.state.searchResultsArr[i].review_score] = [];

      }

      //this is to bundle all books with the same author or review score together so we can filter results
      noAutoCompleteDuplicateObj[this.state.searchResultsArr[i].author].push(this.state.searchResultsArr[i]);
      noAutoCompleteDuplicateObj[this.state.searchResultsArr[i].title].push(this.state.searchResultsArr[i]);
      noAutoCompleteDuplicateObj[this.state.searchResultsArr[i].review_score].push(this.state.searchResultsArr[i]);
    }

    console.log('searchSuggestionsArr', searchSuggestionsArr);
    console.log('searchResultsArr', this.state.searchResultsArr);
    console.log(noAutoCompleteDuplicateObj);

    this.setState({ searchSuggestionsArr, noAutoCompleteDuplicateObj });
  }

  onChangeInput = (e) => {
    this.setState({ selectedOption: e.target.value });
  }

  componentDidMount() {
    // Once page loads hit the post request to get books
    this.getBooks()
      .catch(err => console.log(err));
  }

  getBooks = async () => {
    const searchResultsArr = [];

    axios.post('https://di37ol03g7.execute-api.us-west-2.amazonaws.com/dev/')
      .then((res) => {
        console.log(res.data.results);

        //filter book results whose score are higher than 75
        for (let i = 0; i <= res.data.results.length - 1; i++) {
          if (res.data.results[i].review_score > 74) searchResultsArr.push(res.data.results[i]);
        }

        //if there are more than 100 results take the highest scores
        if (searchResultsArr.length > 100) {
          searchResultsArr.sort((a, b) => {
            if (a.review_score < b.review_score)
              return -1;
            if (a.review_score > b.review_score)
              return 1;
            return 0;
          });

          //cut off elements not in highest 100 scores
          searchResultsArr.splice(100);
        }

        //this is to sort by title
        searchResultsArr.sort((a, b) => {
          if (a.title < b.title)
            return -1;
          if (a.title > b.title)
            return 1;
          return 0;
        })

        this.setState({ searchResultsArr });
        this.buildSearchSuggestionsArr();
      })
      .catch((error) => {
        console.log(error);
      });
  };

  //this is where the filtering magic happens
  handleChange = (selectedOption) => {
    this.setState({ selectedOption, searchResultsArr: this.state.noAutoCompleteDuplicateObj[selectedOption.value] });
  }


  render() {
    //display book results
    const resultsListItems = this.state.searchResultsArr.map((book, i) => {
      return <li className="row" key={i}>
          <p>Title: {book.title} </p>
          <p>Author: {book.author} </p>
          <p>Review Score: {book.review_score} </p>
          <p>Summary: {book.summary} </p>
      </li>
    })

    return (
      <div className="App">
        <header className="App-header">
          <h1 className="App-title">Top Rated Books!</h1>
        </header>
        <div onKeyUp={this.onChangeInput}>
          <Select
            value={this.state.selectedOption}
            onChange={this.handleChange}
            options={this.state.searchSuggestionsArr}
            />
        </div>
        <div>
          <ol>
            { resultsListItems }
          </ol>
        </div>
        <Bar
          data={this.state.barData}
          width={100}
          height={50}
          options={{
            maintainAspectRatio: false
          }}
          />
      </div>
    );
  }
}

export default App;
