import React, { Component } from 'react';
import axios from 'axios';
// import Autocomplete from 'react-autocomplete';
import Select from 'react-select';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  state = {
    data: null,
    selectedOption: '',
    searchSuggestionsArr: [
      { label: 'apple', value: 'apple' },
      { label: 'banana', value: 'banana' },
      { label: 'pear', value: 'pear' },
      { label: 'appless', value: 'appless' },
      { label: 'bananas', value: 'bananas' },
      { label: 'pears', value: 'pears' },
    ],
    searchResultsArr: [],
  };

  // this.onChangeSearchText = this.onChangeSearchText.bind(this);

  onChangeSearchText = () => {
    const searchSuggestionsArr = [];
    const noAutoCompleteDuplicateObj = {};
    const searchResultsArr = [];

    for (let i = 1; i <= this.state.searchResultsArr.length - 1; i++) {
      let addToSearchResultsArr = false;

      if (this.state.searchResultsArr[i].author.includes(this.state.selectedOption)) {
        //this is to prevent duplicate auto suggestions
        if (!noAutoCompleteDuplicateObj[this.state.searchResultsArr[i].author]) {
          searchSuggestionsArr.push({ label: this.state.searchResultsArr[i].author });
          noAutoCompleteDuplicateObj[this.state.searchResultsArr[i].author] = [];
          //this is to bundle all books by the same author together incase we need it later
        } else noAutoCompleteDuplicateObj[this.state.searchResultsArr[i].author].push(this.state.searchResultsArr[i]);
        addToSearchResultsArr = true;
      }

      if (this.state.searchResultsArr[i].title.includes(this.state.selectedOption)) {
        searchSuggestionsArr.push({ label: this.state.searchResultsArr[i].title });
        addToSearchResultsArr = true;
      }

      if (this.state.searchResultsArr[i].review_score.includes(this.state.selectedOption)) {
        searchSuggestionsArr.push({ label: this.state.searchResultsArr[i].review_score });
        //this is to prevent duplicate auto suggestions
        if (!noAutoCompleteDuplicateObj[this.state.searchResultsArr[i].review_score]) {
          searchSuggestionsArr.push({ label: this.state.searchResultsArr[i].review_score });
          noAutoCompleteDuplicateObj[this.state.searchResultsArr[i].review_score] = [];
          //this is to bundle all books with the same review score together incase we need it later
        } else noAutoCompleteDuplicateObj[this.state.searchResultsArr[i].review_score].push(this.state.searchResultsArr[i]);
        addToSearchResultsArr = true;
      }

      if (addToSearchResultsArr) searchResultsArr.push(this.state.searchResultsArr[i]);
    }
  }

  componentDidMount() {
      // Call our fetch function below once the component mounts
    this.callBackendAPI()
      .then(res => this.setState({ data: res.express }))
      .catch(err => console.log(err));
  }
    // Fetches our GET route from the Express server. (Note the route we are fetching matches the GET route from server.js
  callBackendAPI = async () => {
    const response = await fetch('/express_backend');
    const body = await response.json();
    const searchResultsArr = [];

    axios.post('https://di37ol03g7.execute-api.us-west-2.amazonaws.com/dev/')
      .then((res) => {
        console.log(res.data.results);
        for (let i = 0; i <= res.data.results.length - 1; i++) {
          if (res.data.results[i].review_score > 74) searchResultsArr.push(res.data.results[i]);
        }
        //display top 100 books whose score are higher than 75
        //if there are more than 100 results take the highest scores
        if (searchResultsArr.length > 100) {
          searchResultsArr.sort((a, b) => {
            if (a.review_score < b.review_score)
              return -1;
            if (a.review_score > b.review_score)
              return 1;
            return 0;
          })
          //cut off elements not in highest 100 scores
          searchResultsArr.splice(100);
        }
        //this is to sort by title
        searchResultsArr.sort((a, b) => {
          if (a.title < b.title)
            return -1;
          if (a.title > b.title)
            return 1;
          return 0;
        })

        this.setState({ searchResultsArr });

        console.log(this.state.searchResultsArr);
      })
      .catch((error) => {
        console.log(error);
      });

    if (response.status !== 200) {
      throw Error(body.message)
    }
    return body;
  };

  handleChange = (selectedOption) => {
    this.setState({ selectedOption });
    console.log(`Option selected:`, selectedOption);
  }
  // onChange={this.setState({ selectedOption : this.state.selectedOption }); console.log(this.state.selectedOption);}}

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <h1 className="App-title">Welcome to React!</h1>
        </header>
        <Select
          value={this.state.selectedOption}
          onChange={this.handleChange}
          options={this.state.searchSuggestionsArr}
          />
        <p className="App-intro">{this.state.data}</p>
      </div>
    );
  }
}
// <Autocomplete
//   getItemValue={(item) => item.label}
//   items={this.state.searchSuggestionsArr}
//   renderItem={(item, isHighlighted) =>
//     <div style={{ background: isHighlighted ? 'lightgray' : 'white' }}>
//       {item.label}
//     </div>
//   }
//   value={this.state.selectedOption}
//   onChange={(e) => {this.setState({ selectedOption : e.target.value }); console.log(this.state.selectedOption);}}
//   onSelect={(val) => this.setState({ selectedOption : val })}
//   />

export default App;
